-- โครงสร้างตาราง `db_service`
--

CREATE TABLE `db_service` (
  `id` int(20) NOT NULL,
  `url_name` text DEFAULT NULL,
  `keyword` text DEFAULT NULL,
  `service_name` varchar(100) DEFAULT '',
  `description` text DEFAULT NULL,
  `is_active` int(2) DEFAULT 1,
  `is_sub` int(2) DEFAULT 0,
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- dump ตาราง `db_service`
--

INSERT INTO `db_service` (`id`, `url_name`, `keyword`, `service_name`, `description`, `is_active`, `is_sub`, `created`, `updated`) VALUES
(1, 'บริการด้านกฎหมาย', 'บริการด้านกฎหมาย, งานกฎหมายธุรกิจ, งานกฎหมายภาษีอากร, งานอรรถคดีและบังคับคดี, งานกฎหมายอื่น ๆ ที่เกี่ยวกับการค้าและบุคคล', 'บริการด้านกฎหมาย', '<h3><strong>1. งานกฎหมายธุรกิจ</strong></h3>\r\n\r\n<p>ให้คำปรึกษากฎหมายทุกประเภท วางระบบนิติกรรมสัญญา และจัดทำนิติกรรมสัญญา จัดทำเอกสารทางกฎหมายและดำเนินงานทางกฎหมายเกี่ยวกับธุรกิจการค้าให้แก่บุคคล นิติบุคคล รัฐวิสาหกิจ ทุกประเภท</p>\r\n\r\n<h3><strong>2. งานกฎหมายภาษีอากร</strong></h3>\r\n\r\n<p>ให้คำปรึกษากฎหมายภาษีอากร การศึกษาวิเคราะห์โครงสร้างธุรกิจ การวางแผนภาษีของธุรกิจแต่ละประเภท การตรวจสอบภาษีอากร &nbsp;การอบรม ให้คำแนะนำหลักและวิธีปฏิบัติทางภาษีอากร การพบและชี้แจงเจ้าพนักงานภาษีอากร การอุทรณ์คำสั่งเจ้าหน้าที่สรรพากร การฟ้องร้องและต่อสู้คดีภาษีอากร</p>\r\n\r\n<h3><strong>3. งานอรรถคดีและบังคับคดี</strong></h3>\r\n\r\n<p>คำเนินการฟ้องร้อง ต่อสู้คดีทุกประเภททุกศาลทั่วราชอาณาจักร เช่น คดีแพ่ง คดีอาญา คดีแรงงาน คดีทรัพย์สินทางปัญญาและการค้าระหว่างประเทศ คดีปกครอง และคดีตามกฎหมายมหาชนต่าง ๆ การระงับข้อพิพาททางอนุญาโตตุลาการ การบังคับคดีเพื่อให้ลูกค้าชำระหนี้ตามคำพิพากษาและคำชี้ขาดของอนุญาโตตุลาการ</p>\r\n\r\n<h3><strong>4. งานกฎหมายอื่น ๆ ที่เกี่ยวกับการค้าและบุคคล</strong></h3>\r\n\r\n<p>ให้บริการเกี่ยวกับงานจดทะเบียนตามที่กฎหมายทุกประเภทฐ เช่น เครื่องหมายการค้า สิทธิบัตร จดทะเบียนนิติกรรม อสังหาริมทรัพย์ รังวัดสอบที่ดิน จดทะเบียนบริษัท ห้างหุ้นส่วนจำกัด มูลนิธิและสมาคม Visa Work Permit งานเลขานุการจัดประชุมผู้ถือหุ้นของบริษัทจำกัดและบริษัทมหาชนจำกัด &nbsp;ฯลฯ</p>\r\n', 1, 0, '2020-05-20 22:51:05', '2020-05-22 22:01:33'),
(2, 'บริการด้านการจัดทำบัญชีและเงินเดือน', 'บริการจัดทำบัญชี, บริการจัดทำเงินเดือน', 'บริการด้านการจัดทำบัญชีและเงินเดือน', '<h3><strong>1.บริการจัดทำบัญชี</strong></h3>\r\n\r\n<p>บริการจัดทำบัญชีตามมาตรฐานการรายงานทางการเงินและกฎหมายที่กำหนด จัดทำและยื่นแบบภาษี จัดทำสมุดบัญชีต่าง ๆ และบัญชีแยกประเภท <br>จัดทำงบการเงินและรายละเอียดประกอบงบการเงิน ขึ้นทะเบียนผู้ทำบัญชี บริการให้คำปรึกษาและแนะนำด้านบัญชีและภาษีอากร</p>\r\n\r\n<h4><i class=\"fas fa-legal\" style=\"transform: scaleX(-1);\" aria-hidden=\"true\"></i><a href=\"https://lawyerhouse.co.th/services/7/งานบริการจัดทำบัญชี\"> งานบริการจัดทำบัญชี (Account Outsourcing Services)</a></h4>\r\n\r\n<br><h3><strong>2.บริการจัดทำเงินเดือน</strong></h3>\r\n\r\n<p>บริการการจัดทำข้อมูลและคำนวณเงินเดือน ภาษี ประกันสังคมและกองทุนสำรองเลี้ยงชีพ ให้คำปรึกษากาคำนวณภาษีเงินได้บุคคลธรรมดาโดยผู้เชี่ยวชาญได้อย่างถูกต้องและมีประสิทธิภาพ รวมทั้งให้ความสำคัญในการรักษาความลับของลูกค้า</p>\r\n\r\n<h4><i class=\"fas fa-legal\" style=\"transform: scaleX(-1);\" aria-hidden=\"true\"></i><a href=\"https://lawyerhouse.co.th/services/8/งานบริการจัดทำเงินเดือน\"> งานบริการจัดทำเงินเดือน (Payroll Outsourcing Services)</a></h4>\r\n', 1, 0, '2020-05-20 22:55:58', '2020-05-22 22:15:18'),
(3, 'บริการตรวจสอบภายใน', 'บริการตรวจสอบภายใน', 'บริการตรวจสอบภายใน', '<p>การตรวจสอบภายใน เป็นการให้หลักประกันอย่างเที่ยงธรรมและการให้ คำปรึกษาอย่างเป็นอิสระ ซึ่งจัดให้มีขึ้นเพื่อเพิ่มคุณค่าและปรับปรุงการปฏิบัติงานขององค์กรให้ดีขึ้น การตรวจสอบภายในช่วยให้องค์กรบรรลุถึงเป้าหมายที่วางไว้ ด้วยการประเมินและปรับปรุงประสิทธิภาพ ของ กระบวนการบริหารความเสี่ยง การควบคุมและการกากับดูแล</p>\r\n', 1, 0, '2020-05-20 22:57:52', '2020-05-22 22:18:18'),
(4, 'บริการฝึกอบรมสัมมนา', 'บริการฝึกอบรมสัมมนา', 'บริการฝึกอบรมสัมมนา', '<p>บริการจัดฝึกอบรม สัมมนา ภายในหน่วยงาน ซึ่งมีบุคลากรที่ต้องการพัฒนาเป็นจำนวนมาก<br />\r\nการฝึกอบรม/สัมมนาภายในจะทำให้เกิดการพัฒนาอย่างยั่งยืน</p>\r\n', 1, 0, '2020-05-20 22:58:28', '2020-05-22 22:25:21'),
(5, 'บริการจดทะเบียนธุรกิจ', 'บริการจดทะเบียนธุรกิจ', 'บริการจดทะเบียนธุรกิจ', '<p>&bull;&nbsp; จดทะเบียนตั้งบริษัท ห้างหุ้นส่วนจำกัด เปลี่ยนแปลงแก้ไข ได้แก่ ชื่อ, ที่อยู่, กรรมการ,<br />\r\nวัตถุประสงค์, รายชื่อผู้ถือ หุ้น,เพิ่มทุน ลดทุน ควบกิจการ &nbsp;เปลี่ยนแปลง ต่างๆ<br />\r\n&bull;&nbsp; จดทะเบียนภาษีมูลค่าเพิ่ม ขอมีเลขประจำตัวผู้เสียภาษี ขึ้นทะเบียนประกันสังคม<br />\r\n&bull;&nbsp; จดทะเบียนเลิกกิจการ เสร็จชำระบัญชี<br />\r\n&bull;&nbsp; จดทะเบียนการค้าและพาณิชย์ ร้านค้า คณะบุคคล มูลนิธิ<br />\r\n&bull;&nbsp; จดทะเบียนเครื่องหมายการค้า เครื่องหมายบริการ ลิขสิทธิ์ สิทธิบัตร สิทธิบัตรการออกแบบผลิตภัณฑ์</p>\r\n', 1, 0, '2020-05-20 22:59:22', '2020-05-22 22:26:40'),
(6, 'บริการด้านการสืบสวน', 'บริการด้านการสืบสวน', 'บริการด้านการสืบสวน', '<p>เรามีทีมงานเจ้าหน้าที่ นักสืบ สายลับที่มีความเชี่ยวชาญในการสืบสวน ผู้เชี่ยวชาญด้านเทคโนโลยี เจ้าหน้าที่ผู้มีประสบการณ์ด้านการประสานงานกับหน่วยงานต่างๆ โดยใช้เทคโนโลยีและเครื่องมือที่ทันสมัย เราสามารถเฝ้าสังเกตการณ์และติดตามตลอด 24 ชั่วโมง เพื่อให้ได้ข้อมูลที่น่าเชื่อถือ และสามารถใช้เป็นพยานหลักฐานดำเนินคดีในชั้นศาลได้ เช่น การทุจริตและการยักยอก การประกันภัย คนหาย การค้นหาทรัพย์สิน การสืบค้นประวัติทางราชการ การสืบสวนความเป็นมาของบริษัท การตรวจเจ้าของกรรมสิทธิ์ในที่ดิน การตรวจหาทรัพย์สินเพื่อบังคับคดี การตรวจการจ้างงาน เฝ้าสังเกตการณ์เรื่องที่เกี่ยวกับคู่สมรส และบุคคลที่มีความเกี่ยวพันในครอบครัว ฯลฯ</p>\r\n', 1, 0, '2020-05-20 23:00:12', '2020-05-31 10:07:07'),
(7, 'งานบริการจัดทำบัญชี', 'งานบริการจัดทำบัญชี', 'งานบริการจัดทำบัญชี (Account Outsourcing Services)', '<p>งานบริการ จัดทำบัญชี (Account Outsourcing Services) คือ การให้บริการจัดทำบัญชีตามมาตรฐานการรายงาน ทางการเงินและกฎหมายที่กำหนด เพื่อยื่นภาษีรายเดือนและการปิดงบบัญชีประจำปี โดยงานจะครอบคลุมตั้งแต่งานบริการจัดทำบัญชี (account outsourcing services) คือ การให้บริการจัดทำบัญชีตามมาตรฐานการรายงาน ทางการเงินและกฎหมายที่กำหนด เพื่อยื่นภาษีรายเดือนและการปิดงบบัญชีประจำปี โดยงานจะครอบคลุมตั้งแต่การจัดทำสมุดบัญชีต่าง ๆ ตาม พ.ร.บ. 2543 กำหนด การจัดทำและยื่นแบบภาษีทุกประเภทรวมทั้งประกันสังคม จัดทำงบการเงินพร้อมรายละเอียดประกอบงบการเงิน จัดขึ้นทะเบียนเป็นผู้ทำบัญชีตามที่กฎหมายกำหนด จัดทำและ ยื่นแบบต่อหน่วยงานราชการ รวมถึงการบริการให้คำปรึกษาแนะนำด้านบัญชีและภาษีอากร</p>\r\n\r\n<h3><strong>รูปแบบบริการ</strong></h3>\r\n\r\n  <div class=\"row\">\r\n    <div class=\"col-lg-6 padding-bottom-30\">\r\n      <div class=\"table-responsive\">\r\n        <table class=\"table table-bordered\">\r\n          <thead class=\"table-active\">\r\n            <tr>\r\n              <th class=\"text-center\">ข้อดี</th>\r\n            </tr>\r\n          </thead>\r\n          <tbody>\r\n            <tr>\r\n              <td>1. ตัดปัญหาการรั่วไหลของข้อมูลเงินเดือน</td>\r\n            </tr>\r\n            <tr>\r\n              <td>2. มีผู้เชี่ยวชาญดูแลงานภาษีและกฎหมาย</td>\r\n            </tr>\r\n            <tr>\r\n              <td>3. ประหยัดค่าใช้จ่ายในการดำเนินการ</td>\r\n            </tr>\r\n            <tr>\r\n              <td>4. ตัดปัญหาการลาออกของพนักงานจัดทำเงินเดือน</td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n    </div>\r\n  </div>\r\n    <div class=\"col-lg-6 padding-bottom-30\">\r\n      <div class=\"table-responsive\">\r\n        <table class=\"table table-bordered\">\r\n          <thead class=\"table-active\">\r\n            <tr>\r\n              <th class=\"text-center\">สิ่งที่ลูกค้าต้องเตรียม</th>\r\n            </tr>\r\n          </thead>\r\n          <tbody>\r\n            <tr>\r\n              <td>1. ข้อมูลพนักงาน</td>\r\n            </tr>\r\n            <tr>\r\n              <td>2. เงื่อนไขการจ่ายเงินเดือน</td>\r\n            </tr>\r\n            <tr>\r\n              <td>3. ตัวอย่าง slip เงินเดือน</td>\r\n            </tr>\r\n            <tr>\r\n              <td>4. ตัวอย่างรายงานผู้บริหาร</td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n    </div>\r\n  </div>\r\n    <div class=\"col-lg-6 padding-bottom-30\">\r\n      <div class=\"table-responsive\">\r\n        <table class=\"table table-bordered\">\r\n          <thead class=\"table-active\">\r\n            <tr>\r\n              <th class=\"text-center\">สิ่งที่ลูกค้าต้องเตรียม</th>\r\n            </tr>\r\n          </thead>\r\n          <tbody>\r\n            <tr>\r\n              <td>1. รายงานภาษีซื้อ-ภาษีขาย</td>\r\n            </tr>\r\n            <tr>\r\n              <td>2. เอกสารประกอบการรับ-การจ่าย</td>\r\n            </tr>\r\n            <tr>\r\n              <td>3. Bank statement ขององค์กร</td>\r\n            </tr>\r\n            <tr>\r\n              <td>4. แบบแสดงการยื่นภาษี (พร้อมใบเสร็จ)</td>\r\n            </tr>\r\n            <tr>\r\n              <td>5. หนังสือรับรองการหักภาษี ณ ที่จ่าย</td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n    </div>\r\n  </div>\r\n</div>\r\n', 1, 1, '2020-05-20 23:08:33', '2020-05-22 22:43:38'),
(8, 'งานบริการจัดทำเงินเดือน', 'งานบริการจัดทำเงินเดือน', 'งานบริการจัดทำเงินเดือน (Payroll Outsourcing Services)', '<p>งานบริการ จัดทำเงินเดือน (Payroll Outsourcing Services) คือ การให้บริการจัดทำเงินเดือนพนักงานที่ครอบคลุมตั้งแต่ การคำนวณเงินเดือน ประสานทางด้านธนาคาร ภาษี ประกันสังคม กองทุนสำรองเลี้ยงชีพ และการจัดการงานอื่นๆ ที่เกี่ยวข้อง โดยใช้โปรแกรมคำนวณที่แม่นยำและมีการตรวจสอบความถูกต้องจากผู้เชี่ยวชาญ เฉพาะด้าน เพื่อได้มาซึ่งผลลัพธ์ที่ถูกต้อง สอดคล้องกับกฎหมายข้อบังคับฉบับปัจจุบัน และส่งมอบงานสำเร็จภายในระยะเวลาที่กำหนด</p>\r\n\r\n<h3><strong>รูปแบบบริการ</strong></h3>\r\n\r\n<p>ให้บริการศึกษา วิเคราะห์ และวางแผนงานงานคำนวณภาษีเงินได้ออกให้สำหรับพนักงานในองค์กรแจ้งชื่อพนักงานเข้า-ออกประกันสังคม งานจัดร่างและยื่นแบบ ภ.ง.ด.94. จัดทำและยื่นแบบ ภ.ง.ด.1ก ต่อกรมสรรพากรบริการจัดทำเงินเดือนตามเงื่อนไขขององค์กรและกฎหมายแรงงาน</p>\r\n\r\n<h3><strong>อัตราค่าบริการ</strong></h3>\r\n\r\n<p>ราคาขึ้นอยู่กับจำนวนของพนักงาน เงื่อนไขการจ่ายผลตอบแทน ระยะเวลาในการดำเนินงานและรายละเอียดรายงานผู้บริหาร</p>\r\n\r\n<div class=\"row\">\r\n    <div class=\"col-lg-12 padding-bottom-30\">\r\n      <div class=\"table-responsive\">\r\n        <table class=\"table table-bordered\">\r\n          <thead class=\"table-active\">\r\n            <tr>\r\n              <th class=\"text-center\">บริการนี้เหมาะสำหรับ</th>\r\n            </tr>\r\n          </thead>\r\n          <tbody>\r\n            <tr>\r\n              <td>1. บริษัทเอกชน</td>\r\n            </tr>\r\n            <tr>\r\n              <td>2. หน่วยงานรัฐวิสาหกิจ</td>\r\n            </tr>\r\n            <tr>\r\n              <td>3. หน่วยงานไม่แสวงหาผลกำไร    (มูลนิธิ, สมาคม)</td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n    </div>\r\n  </div>\r\n</div>\r\n', 1, 1, '2020-05-20 23:10:49', '2020-05-22 22:46:12'),
(9, 'Visa&Work permit', 'visa,workpermit', 'Visa & Work permit', '<h3>Visa &amp; work permit</h3>\r\n\r\n<p>หากคุณกำลัง วิตกกังวลกับการขอ Visa &amp; work permit ไม่ทราบขั้นตอนการดำเนินการ เอกสารการเงินไม่พร้อม ไม่มีเวลาเดินทางไป สถานทูต หรือกลัวถูกปฏิเสธวีซ่า ไม่มีความมั่นใจ บริษัท ลอว์เยอร์ เฮ้าส์ จำกัด เข้าใจปัญหาเหล่านี้เป็นอย่างดี เราพร้อมจะแก้ปัญหาให้ท่าน เรามีเจ้าหน้าที่มืออาชีพพูดภาษาอังกฤษมีความยินดีและพร้อมให้คำปรึกษาคุณทุกเรื่องของ Visa &amp; work permit</p>\r\n', 1, 0, '2020-05-25 21:45:00', '2020-05-31 21:33:33'),
(10, 'อสังหาริมทรัพย์', 'การซื้อขายที่ดิน คอนโดมิเนียม การถือครองอสังหาริมทรัพย์ในประเทศไทย', 'อสังหาริมทรัพย์', '', 1, 0, '2021-03-04 13:05:36', NULL);

