INSERT INTO `db_team` (`id`, `name`, `name_en`, `position`, `position_en`, `description`, `description_en`, `img_cover`, `is_active`, `created`, `updated`) VALUES
(1, 'นายสงกรานต์ณรงค์ สุขอนันต์', 'นายสงกรานต์ณรงค์ สุขอนันต์', 'ทนายความ', 'Lawyer', '<ul>\r\n	<li>\r\n	<p>นิติศาสตร์บัณฑิต มหาวิทยาลัยรามคำแหง รุ่น 28</p>\r\n	</li>\r\n	<li>\r\n	<p>หลักสูตรวิชาว่าความของสำนักฝึกอบรมวิชาว่าความแห่งสภาทนายความ รุ่นที่ 20</p>\r\n	</li>\r\n	<li>\r\n	<p>เนติบัณฑิตไทย เนติบัณฑิตยสภา สมัยที่ 56</p>\r\n	</li>\r\n	<li>\r\n	<p>ทนายความสิ่งแวดล้อมรุ่น 10</p>\r\n	</li>\r\n	<li>\r\n	<p>ผู้ทรงคุณวุฒิจัดทำสัญญาจ้างบริหารก่อสร้าง</p>\r\n	</li>\r\n	<li>\r\n	<p>เจ้าหน้าที่ความปลอดภัยระดับบริหาร</p>\r\n	</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n	<p>Bachelor of Laws (LL. B) Degree, Ramkhamhaeng University</p>\r\n	</li>\r\n	<li>\r\n	<p>Barrister-at-Law from Thai Barrister Institute</p>\r\n	</li>\r\n	<li>\r\n	<p>Lawyer&rsquo;s license of the Lawyers Council, Thailand</p>\r\n	</li>\r\n	<li>\r\n	<p>Environmental lawyer</p>\r\n	</li>\r\n	<li>\r\n	<p>A construction management contracts senior manager.</p>\r\n	</li>\r\n	<li>\r\n	<p>Safety Officer in Management Level.</p>\r\n	</li>\r\n</ul>\r\n', 'songkrannarong2.png', 1, '2021-03-27 00:35:04', '2021-03-31 22:00:35'),
(2, 'นายทวีศักดิ์ ปานสีนุ่น', 'นายทวีศักดิ์ ปานสีนุ่น', 'ทนายความ', 'Lawyer', '<ul>\r\n	<li>\r\n	<p>นิติศาสตร์บัณฑิต มหาวิทยาลัยรามคำแหง รุ่น 28</p>\r\n	</li>\r\n	<li>\r\n	<p>หลักสูตรวิชาว่าความของสำนักฝึกอบรมวิชาว่าความแห่งสภาทนายความ รุ่นที่ 22</p>\r\n	</li>\r\n	<li>\r\n	<p>เจ้าหน้าที่นิติกรรม สัญญา จดทะเบียนสิทธิให้เป็นไปตามกฎหมาย</p>\r\n	</li>\r\n	<li>\r\n	<p>เจ้าหน้าที่จัดการทรัพย์สินของลูกหนี้ และเจ้าหน้าที่สินเชื่อธุรกิจ ธนาคารไทยพาณิชย์</p>\r\n	</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n	<p>Bachelor of Laws (LL. B) Degree, Ramkhamhaeng University</p>\r\n	</li>\r\n	<li>\r\n	<p>Lawyer&rsquo;s license of the Lawyers Council, Thailand</p>\r\n	</li>\r\n	<li>\r\n	<p>Legal contract officer</p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"https://dict.longdo.com/search/comptroller%20in%20bankruptcy\">Comptroller in bankruptcy</a> and <a href=\"https://dictionary.sanook.com/search/dict-user/commercial-credit-officer\">Commercial Credit Officer</a>, The Siam Commercial Bank Public Company Limited</p>\r\n	</li>\r\n</ul>\r\n', 'taweesak.png', 1, '2021-03-27 01:03:35', '2021-03-31 22:00:08'),
(3, 'นายภานุมาศ บัวภา', 'นายภานุมาศ บัวภา', 'ทนายความ', 'Lawyer', '<ul>\r\n	<li>\r\n	<p>นิติศาสตร์บัณฑิต มหาวิทยาลัยรามคำแหง</p>\r\n	</li>\r\n	<li>\r\n	<p>หลักสูตรวิชาว่าความของสำนักฝึกอบรมวิชาว่าความแห่งสภาทนายความ รุ่นที่ 31</p>\r\n	</li>\r\n	<li>\r\n	<p>เนติบัณฑิตไทย เนติบัณฑิตยสภา สมัยที่ 71</p>\r\n	</li>\r\n	<li>\r\n	<p>ผ่านการฝึกงานโครงการนักศึกษาฝึกงานคณะนิติศาสตร์ รุ่นที่ 19 ณ สำนักงานอัยการสูงสุด</p>\r\n	</li>\r\n	<li>\r\n	<p>ผ่านการฝึกงานในสถาบันพัฒนาข้าราชการฝ่ายอัยการ สำนักงานอัยการสูงสุด</p>\r\n	</li>\r\n	<li>\r\n	<p>ที่ปรึกษากฎหมายนิติบุคคลหมู่บ้านจัดสรร</p>\r\n	</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n	<p>Bachelor of Laws (LL. B) Degree, Ramkhamhaeng University</p>\r\n	</li>\r\n	<li>\r\n	<p>Lawyer&rsquo;s license of the Lawyers Council, Thailand</p>\r\n	</li>\r\n	<li>\r\n	<p>Barrister-at-Law from Thai Barrister Institute</p>\r\n	</li>\r\n	<li>\r\n	<p>Internship Certificate of the Office of The Attorney General, Thailand</p>\r\n	</li>\r\n	<li>\r\n	<p>Internship Certificate of Training and Development Institute Office of the Attorney General.</p>\r\n	</li>\r\n	<li>\r\n	<p>Legal Consulting on <a href=\"https://so05.tci-thaijo.org/index.php/hssnsru/article/view/241630\">Housing Estate Juristic Person Law</a></p>\r\n	</li>\r\n</ul>\r\n', 'panumas.png', 1, '2021-03-27 01:04:09', '2021-03-31 21:58:28'),
(4, 'นางสาวปานชนก ธนาวุฒิ', 'นางสาวปานชนก ธนาวุฒิ', 'ทนายความ', 'Lawyer', '<ul>\r\n	<li>\r\n	<p>นิติศาสตร์บัณฑิต จุฬาลงกรณ์มหาวิทยาลัย</p>\r\n	</li>\r\n	<li>\r\n	<p>นิติศาสตร์มหาบัณฑิต จุฬาลงกรณ์มหาวิทยาลัย</p>\r\n	</li>\r\n	<li>\r\n	<p>หลักสูตรวิชาว่าความของสำนักฝึกอบรมวิชาว่าความแห่งสภาทนายความ รุ่น 21</p>\r\n	</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n	<p>Bachelor of Laws (LL. B) Degree, Chulalongkorn University</p>\r\n	</li>\r\n	<li>\r\n	<p>Master of Laws Degree, Chulalongkorn University</p>\r\n	</li>\r\n	<li>\r\n	<p>Lawyer&rsquo;s license of the Lawyers Council, Thailand</p>\r\n	</li>\r\n</ul>\r\n', 'panchanok.png', 1, '2021-03-27 01:04:36', '2021-03-31 22:01:24');

